<?phpinclude_once(dirname(__FILE__) .'/Helper.php');Class Upload {    private $image_name;    public function getImagename($directory) {        $dir_dest = $directory;                //Existing Image Name with its extention                $base_name=basename($_FILES["image_name"]["name"]);        $new_extention = '.png';                $file_size=$_FILES["image_name"]["size"];        //Create a random id and combine it with the new extention                $img_name = $this->randamId() . $new_extention;        $image_file_dir = $dir_dest . $img_name;        $current_extention = strtolower(pathinfo($base_name, PATHINFO_EXTENSION));        if ($file_size> 500000) {            echo "<script>Sorry, your file is too large</script>";            exit();                    } else if ($current_extention != "jpg" && $current_extention != "png" && $current_extention != "jpeg") {            echo "<script>Please Uopload the image with a Valid Extention.</script>";            exit();                    } else if (move_uploaded_file($_FILES["image_name"]["tmp_name"], $image_file_dir)) {            return $this->image_name = $img_name;        }    }    public function randamId() {        $today = time();        $startDate = date('YmdHi', strtotime('1912-03-14 09:06:00'));        $range = $today - $startDate;        $rand = rand(0, $range);        $randam = $rand . "_" . ($startDate + $rand) . '_' . $today . "_n";        return $randam;    }}